document.querySelector('.form-subscribe').addEventListener('submit', function(e) {
  e.preventDefault(); 
  const form = e.target;
  const emailInput = form.querySelector('#section-subscription-email');
  const email = emailInput.value;
  const subscribeButton = form.querySelector('.button');
  const labelDefault = subscribeButton.querySelector('.label-default');
  const labelLoading = subscribeButton.querySelector('.label-loading');
  const labelLoaded = subscribeButton.querySelector('.label-loaded');
  const alertMessage = form.querySelector('.form-subscribe-alert');
  const successMessage = alertMessage.querySelector('.success');
  const errorMessage = alertMessage.querySelector('.error');

  labelDefault.style.display = 'none';
  labelLoading.style.display = 'flex';
  successMessage.style.display = 'none';
  errorMessage.style.display = 'none';

  setTimeout(() => {

    const isSuccessful = Math.random() > 0.3; 
    labelLoading.style.display = 'none';

    if (isSuccessful) {
      labelLoaded.style.display = 'flex';
      successMessage.style.display = 'block'; 
    } else {
      labelDefault.style.display = 'inline-block';
      errorMessage.style.display = 'block'; 
    }

    setTimeout(() => {
      labelLoaded.style.display = 'none';
      labelDefault.style.display = 'inline-block';
      successMessage.style.display = 'none';
      errorMessage.style.display = 'none'; 
      emailInput.value = ''; 
    }, 3000); 
  }, 2000);
});


