function getThumbnail(e){let t=e.media$thumbnail?e.media$thumbnail.url:null;if(!t){let o=/<img[^>]+src="([^">]+)"/g.exec(e.content?e.content.$t:e.summary.$t);o&&o[1]&&(t=o[1])}return t?t.replace(/s72(-w\d+-h\d+)?-c/,"s16000"):"https://i.imgur.com/NJFjWhm.jpeg"}function timeAgo(e){let t=Math.floor((new Date-e)/1e3),o={year:31536e3,month:2592e3,week:604800,day:86400,hour:3600,minute:60,second:1};for(let n in o){let r=Math.floor(t/o[n]);if(r>=1)return`${r} ${n}${r>1?"s":""} ago`}return"just now"}$(document).ready(function(){let e=$(".scroll-progress");$(window).on("scroll",function(){$(this).scrollTop()>300?e.addClass("scroll-progress-show"):e.removeClass("scroll-progress-show")}),$(".scroll-progress-button").on("click",function(){$("html, body").animate({scrollTop:0},400)})}),document.addEventListener("DOMContentLoaded",function(){let e=document.querySelector(".post-meta-time");if(e){let t=e.textContent.trim(),o=new Date(t),n=timeAgo(o);e.textContent=n}});
