$(document).ready(function() {
    const blogUrl = window.location.origin;
    const $swiperWrapper = $('.slider-featured > .swiper-wrapper');
    const label = $swiperWrapper.attr('data-label');
    let feedUrl;
    if (label === "recent") {
        feedUrl = `${blogUrl}/feeds/posts/default?alt=json`;
    } else {
        feedUrl = `${blogUrl}/feeds/posts/default/-/${label}?alt=json`;
    }

    const maxPosts = 5;  // Số bài tối đa mỗi lần tải trong slider
    $.ajax({
        url: feedUrl,
        method: 'GET',
        dataType: 'jsonp',
        success: function(response) {
            let item = '';
            const entries = response.feed.entry;
            if (entries && entries.length > 0) {
                const limitedEntries = entries.slice(0, maxPosts);
                $.each(limitedEntries, function(i, entry) {
                    const title = entry.title.$t;
                    const link = entry.link.find(l => l.rel === 'alternate').href;
                    const publishedDate = timeAgo(new Date(entry.published.$t));
                    const contentSnippet = entry.content ? entry.content.$t : entry.summary.$t;
                    const thumbnail = getThumbnail(entry);
                    const tags = entry.category && entry.category.length > 0 ? entry.category.map(cat => `<a class='card-post-tag' href='/search/label/${cat.term}/'>${cat.term}</a>`).join('<span class="card-post-meta-divider">•</span>') : '<span class="no-label">No Label</span>';
                    item += `<div class="swiper-slide"><div class="card-post card-post-featured card-has-image card-has-slider card-has-author"><a class="card-post-image" href="${link}" tabindex="-1"><img alt="${title}" height="530" loading="eager" src="${thumbnail}" width="1340"/> <span class="card-post-image-badge"><span class="card-post-time">${publishedDate}</span></span></a><div class="card-post-content"><div class="card-post-meta">${tags}</div><h3 class="card-post-title"><a href="${link}">${title}</a></h3><a aria-label="Read More: ${title}" class="card-post-button button" href="${link}"><span class="label">Read More</span><svg class="icon icon-arrow-right" fill="none" height="20" viewBox="0 0 20 20" width="20" xmlns="https://www.w3.org/2000/svg"><path d="M13 6.16675L16.8333 10.0001L13 13.8334" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="1.5"/><path d="M3.16669 10H16.8333" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="1.5"/></svg></a></div></div></div>`;
                });
                $swiperWrapper.html(item);
                $swiperWrapper.removeClass('spinner-container');
            } else {
                $swiperWrapper.html('<p>No posts found for this label.</p>');
                $swiperWrapper.removeClass('spinner-container');
            }
        },
        error: function() {
            $swiperWrapper.html('<p>Error loading feed. Please try again later.</p>');
            $swiperWrapper.removeClass('spinner-container');
        }
    });
});
