$(document).ready(function() {
    const blogUrl = window.location.origin;
    const $postList = $('.posts-list');
    const label = $postList.attr('data-label');
    const maxPostsPerPage = parseInt($postList.attr('data-posts'), 10);  // Số bài tối đa mỗi lần tải
    let startIndex = 1;  // Bắt đầu từ bài viết đầu tiên
    let isEnd = false;  // Đánh dấu khi tải hết bài
    let feedUrl;

    // Xử lý URL feed
    if (label === "recent") {
        feedUrl = `${blogUrl}/feeds/posts/default?alt=json`;
    } else {
        feedUrl = `${blogUrl}/feeds/posts/default/-/${label}?alt=json`;
    }

    function loadPosts() {
        if (isEnd) return;

        $('.pagination-button-next .loader').show();
        $('.pagination-button-next .label').hide();

        $.ajax({
            url: feedUrl + `&start-index=${startIndex}&max-results=${maxPostsPerPage}`, // Cập nhật start-index và max-results
            method: 'GET',
            dataType: 'jsonp',
            success: function(response) {
                let item = '';
                const entries = response.feed.entry;
                if (entries && entries.length > 0) {
                    $.each(entries, function(i, entry) {
                        const title = entry.title.$t;
                        const link = entry.link.find(l => l.rel === 'alternate').href;
                        const publishedDate = timeAgo(new Date(entry.published.$t));
                        const thumbnail = getThumbnail(entry);
                        const tags = entry.category && entry.category.length > 0 ? entry.category.map(cat => `<a class='card-post-tag' href='/search/label/${cat.term}/'>${cat.term}</a>`).join('<span class="card-post-meta-divider">•</span>') : '<span class="no-label">No Label</span>';
                        item += `<div><article class="card card-post"><a class="card-post-image" href="${link}"><img alt="${title}" src="${thumbnail}" loading="lazy"> <span class="card-post-image-badge"><span class="card-post-time">${publishedDate}</span></span></a><div class="card-post-content"><h3 class="card-post-title"><a href="${link}">${title}</a></h3><div class="card-post-meta">${tags}</div></div></article></div>`;
                    });
                    $postList.append(item);
                    $postList.removeClass('spinner-container');
                    startIndex += maxPostsPerPage;  // Tăng startIndex cho lần tiếp theo
                    if (startIndex > response.feed.openSearch$totalResults.$t) {
                        isEnd = true;
                        $('.pagination-button-next').addClass('complete').text("You've reached the end of the list");
                    }
                } else {
                    isEnd = true;
                    $('.pagination-button-next').addClass('complete').text("No posts found.");
                }
            },
            error: function() {
                $postList.html('<p>Error loading feed. Please try again later.</p>');
                $postList.removeClass('spinner-container');
            },
            complete: function() {
                $('.pagination-button-next .loader').hide();
                if (!isEnd) {
                    $('.pagination-button-next .label').show();
                }
                $postList.removeClass('spinner-container');
            }
        });
    }

    loadPosts();

    $('.pagination-button-next').on('click', function(e) {
        e.preventDefault();
        loadPosts();
    });
});
