$(document).ready(function(){const blogUrl=window.location.origin;const maxPosts=15;$('.slider-posts > .swiper-wrapper').each(function(){const $sliderWrapper=$(this);const label=$sliderWrapper.attr('data-label');let feedUrl;if(label==="recent"){feedUrl=`${blogUrl}/feeds/posts/default?alt=json`}else{feedUrl=`${blogUrl}/feeds/posts/default/-/${label}?alt=json`}
