$(document).ready(function(){const blogUrl=window.location.origin;const $postList=$('.posts-list');const label=$postList.attr('data-label');const maxPostsPerPage=parseInt($postList.attr('data-posts'),10);let startIndex=0;let isEnd=!1;const query=$postList.attr('data-query')||'';let feedUrl;if(label){feedUrl=`${blogUrl}/feeds/posts/default/-/${label}?alt=json`}else{feedUrl=`${blogUrl}/feeds/posts/default?alt=json`;if(query){feedUrl+=`&q=${encodeURIComponent(query)}`}}
